sys_ui_action.script:
var gr = new GlideRecord('x_pinr_wsc_wsc_request');
gr.newRecord();
gr.initialize();
for(var variable in current) { 
 	gr.setValue(variable,current.getValue(variable));
}
gr.request_status = "Draft";
gr.stage = "Submission";
gr.landtech_approval_status = "";
gr.landman_approval_status = "";
gr.engineer_approval_status = "";
gr.opened_by = gs.getUserID();
gr.opened_at = new GlideDateTime();
gr.insert();


gs.addInfoMessage("WSC request " + current.number + " has been cloned into a a new Draft request " + gr.number + ". Please make appropriate edits in this new request and submit it for approval.");
